import React, { useEffect, useState } from 'react'

function Form() {
    const initialValue = {
        username: '',
        email: '',
        password: ''
    }
    const [formValues, setFormValues] = useState(initialValue);
    const [formErrors,setFormErrors] = usestate({})
    const [issubmit,setsSubmit]= useState(false);

    useEffect(()=>{
        if(Object.keys(formErrors).length === 0 && issubmit){
            prompt
        }
    },[formErrors]);
    const changeHandler = (event) => {
        const { name , value } = event.target;
        setFormValues({...formValues,[name]:value})
    }
    const submitHandler = (event) =>{
        event.preventDefault();
        setFormErrors(validate(formValues))
        setsSubmit(true)
    }
    const validate = (values) => {
        const errors= {};
        const regex = /^[^\s@]+@[^\s@]+\.[^\s@]{2,}$/i;
        if(!values.username){
            errors.username("Username is Required!")
        }
        if(!values.email){
            errors.email("Email is Required!")
        }
        else if(!regex.test(values.email)){
            errors.email("This is not a valid email format.")
        }
        if(!values.password){
            errors.password("Password is Required!")
        }
        else if (values.password.lenght < 4 ){
            errors.password("password must be more than 4 characters")
        }else if (values.password.lenght > 10){
            errors.password("password cannot exceed more than 10 characters!")
        }
        return errors
    };
    return (
        <div className='container'>
            <form onSubmit={submitHandler}>
                <h1>Login Form!</h1>
                <div className='ui form'>
                    <div className='field'>
                        <label>Username</label> 
                        <input type='text' name="username" placeholder="Username" value={formValues.username} onChange={changeHandler}/>
                    </div>
                    <div className='field'>
                        <label>Email</label>
                        <input type='email' name="email" placeholder="Email" values={formValues.email} onChange={changeHandler}/>
                    </div>
                    <div className='field'>
                        <label>Password</label>
                        <input type='password' name="password" placeholder="password" values={formValues.password} onChange={changeHandler}/>
                    </div>
                    <button type="submit" class="btn btn-primary">Submit</button>
                </div>
            </form>

        </div>
    )
}

export default Form
