import React , {useContext} from 'react'
import { useParams } from 'react-router-dom'
import { data } from './Ecom';



function Product() {
    const products = useContext(data);

    let { id } = useParams();

    const filterProduct = products.filter((item)=> item.id == {id})

    return (
    <div>
        {filterProduct}    
   </div>
  )
}

export default Product
